bundle.postinst:
################################################################################ # Setup the msmtprc file from the user supplied parameters # MSMTPRC=/etc/msmtprc cat >${MSMTPRC} <<EOF # MSMTP Configuration # # See https://marlam.de/msmtp/msmtp.html#Configuration-files # host ${BUNDLE_INSTALLATION_HOST} domain ${BUNDLE_INSTALLATION_DOMAIN} from ${BUNDLE_INSTALLATION_FROM}
# If necessary you can turn ON TLS #tls on #tls_certcheck off #tls_trust_file /etc/ssl/certs/ca-certificates.crt EOF
chown root:root ${MSMTPRC} chmod 644 ${MSMTPRC}
